# coding:utf-8import socketdef send_msg(src, desc, msg):    """    模拟飞秋发送消息    :param src: 本机IP    :param desc: 目标IP    :param msg: 发送的消息，消息格式为bytes。    :return:    """    sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)    host = src    port = 2425    sock.connect((desc, 2425))    context = msg    sock.send(context)    # sock.close()if __name__ == '__main__':    msg = "\x31\x3a\x31\x35\x35\x39\x36\x31\x34\x31\x38\x39\x3a\x47\x61\x76" \          "\x69\x6e\x3a\x67\x61\x76\x69\x6e\x73\x75\x6e\x2e\x6c\x6f\x63\x61" \          "\x6c\x3a\x32\x38\x38\x3a\x73\x73\x73\x73\x73\x73\x00\x57\x6f\x72" \          "\x6b\x47\x72\x6f\x75\x70\x00\x30\x30\x2d\x45\x30\x2d\x34\x43\x2d" \          "\x36\x43\x2d\x37\x32\x2d\x35\x38\x00"    print(msg)    print(type(msg))    #  bytes  str    send_msg(src='172.16.44.193', desc='172.16.44.115', msg=bytes(msg,encoding='utf-8'))    print()