from django.urls import path, re_pathfrom blog import viewsurlpatterns = [    path('home/',views.home,name='home'),    path('articles/', views.articles),    re_path('categories/(?P<category_id>[0-9])/$', views.articles_by_category,            name='category'),    path('categories/', views.category_list),    # TODO: 用正则表达式传递文章ID    re_path('detail/(?P<article_id>[0-9])/$', views.article_detail,            name='detail'),]